
const sum =  (a, b) => a+b;
const sub = (a,b) => a-b;
const div = (a,b) => a/b;
const mult = (a,b) => a*b;


var number_one = 15;
var number_two = 3;

console.log( sum(number_one, number_two) )
console.log( sub(number_one, number_two) )
console.log( div(number_one, number_two) )
console.log( mult(number_one, number_two) )